async fn set_up_test_room() -> (PooledConnection<SqliteConnectionManager>, DatabaseConnectionPool) {
    let manager = r2d2_sqlite::SqliteConnectionManager::file("file::memory:");
    let mut flags = OpenFlags::default();
    flags.set(OpenFlags::SQLITE_OPEN_URI, true);

    let manager = manager.with_flags(flags);

    let pool = r2d2::Pool::<r2d2_sqlite::SqliteConnectionManager>::new(manager).unwrap();

    let mut conn = pool.get().unwrap();

    storage::setup_database_with_conn(&mut conn);
    let success = handlers::create_room_with_conn(
        &conn,
        &CreateRoom { token: "test_room".to_string(), name: "Test".to_string() },
    );
    assert!(success.is_ok());

    return (conn, pool);
}

fn get_user(conn: &rusqlite::Connection) -> User {
    // Generate a fake user key pair
    let (_, user_public_key) = crypto::generate_x25519_key_pair();
    let hex_user_public_key = format!("05{}", hex::encode(user_public_key.to_bytes()));

    let result = handlers::insert_or_update_user(conn, &hex_user_public_key);
    assert!(result.is_ok());
    return result.unwrap();
}

#[tokio::test]
async fn test_file_handling() {
    // Ensure the test room is set up and get a database connection pool
    let (mut conn, pool) = set_up_test_room().await;

    let room = storage::get_room_from_token(&conn, "test_room").unwrap();
    // Get an auth token
    let user = get_user(&conn);

    // Store the test file
    let filename: Option<&str> = None;
    let auth = handlers::AuthorizationRequired { upload: true, write: true, ..Default::default() };
    let id =
        match handlers::store_file_impl(&mut conn, &room, &user, auth, TEST_FILE, filename, true)
            .ok()
        {
            Some(mut upload) => {
                let result = upload.commit();
                assert!(result.is_ok());
                Some(upload.id)
            }
            _ => None,
        }
        .unwrap();

    // Retrieve the file and check the content
    let room_id = room.id;
    let response = handlers::get_file_conn(&mut conn, &room, id, user).unwrap();
    let response_bytes = hyper::body::to_bytes(response).await.ok();
    // The expected json response
    let json = GenericStringResponse {
        status_code: StatusCode::OK.as_u16(),
        result: TEST_FILE.to_string(),
    };
    let expected_result = warp::reply::json(&json).into_response();
    let expected_result_bytes = hyper::body::to_bytes(expected_result).await.ok();
    assert_eq!(response_bytes, expected_result_bytes);
    // Prune the file and check that it's gone
    // Will evaluate to now + 60
    let sixty_seconds_ago = SystemTime::now() - Duration::new(60, 0);
    storage::prune_files(&mut conn, &sixty_seconds_ago);
    // It should be gone now
    fs::read(format!("files/{}_files/{}", room_id, id)).unwrap_err();
    // Check that the file record is also gone
    let conn = pool.get().unwrap();
    let raw_query = "SELECT id FROM files";
    let result: Result<String, _> = conn.query_row(&raw_query, params![], |row| Ok(row.get(0)?));
    // It should be gone now
    result.unwrap_err();
}

#[tokio::test]
async fn test_session_version_endpoint() {
    let ios = handlers::get_session_version("ios").await.unwrap();
    assert_eq!(handlers::SESSION_VERSIONS.read().clone()["ios"].1, ios);
    let android = handlers::get_session_version("android").await.unwrap();
    assert_eq!(handlers::SESSION_VERSIONS.read().clone()["android"].1, android);
    let desktop = handlers::get_session_version("desktop").await.unwrap();
    assert_eq!(handlers::SESSION_VERSIONS.read().clone()["desktop"].1, desktop);
}

#[tokio::test]
async fn test_pinned_messages() {
    // Ensure the test room is set up and get a database connection pool
    let (mut conn, _) = set_up_test_room().await;

    let room = storage::get_room_from_token(&conn, "test_room").unwrap();
    // Get an auth token
    let user = get_user(&conn);
    // Pin a message and see if it exists

    // Pinned message should not exist on first run
    let pinned_message = handlers::get_pinned_message_impl(&mut conn, &user, &room);
    assert!(pinned_message.is_err());

    //insert a message into the room
    let data = b"test message";
    let signature = b"test signature";

    let message_id =
        handlers::insert_message_impl(&mut conn, &room, &user, data, signature).unwrap().server_id;

    // Add a pinned message should fail if not moderator
    let result = handlers::pin_message_impl(&mut conn, message_id, &user, &room);
    assert!(result.is_err());

    // Add moderator and test again
    let result = handlers::add_moderator_impl(&mut conn, &user.session_id.as_str(), true, &room);
    assert!(result.is_ok());

    let result = handlers::pin_message_impl(&mut conn, message_id, &user, &room);
    assert!(result.is_ok());

    // Check the pinned message now exists
    let pinned_message = handlers::get_pinned_message_impl(&mut conn, &user, &room).unwrap();
    assert_eq!(pinned_message.message, message_id);

    // Delete the pinned message and check it is no longer there
    let result = handlers::delete_pinned_message_impl(&mut conn, &user, &room);
    assert!(result.is_ok());
    let pinned_message_id = handlers::get_pinned_message_impl(&mut conn, &user, &room);
    assert!(pinned_message_id.is_err());
}

const TEST_FILE: &str = "";
